import project
from project import History
from project import extract_symbol_rate
def test_get_statistics():
    testing=History()
    testing.add_number(56)
    testing.add_number(56)
    testing.add_number(56)
    testing.add_number(56)
    testing.add_number(56)
    testing.add_number(56)
    mean,minimum,maximum=testing.get_statistics()
    assert(mean==56)
    assert(minimum==56)
    assert(maximum==56)

def test_ready():
    testing=History()
    testing.add_number(10)
    testing.add_number(10)
    testing.add_number(10)
    testing.add_number(10)
    assert testing.ready()==False

